CREATE TABLE IF NOT EXISTS
    `mythicaldash_mail_templates` (
        `id` INT NOT NULL AUTO_INCREMENT,
        `name` TEXT NOT NULL,
        `content` TEXT NOT NULL,
        `deleted` ENUM ('false', 'true') NOT NULL DEFAULT 'false',
        `locked` ENUM ('false', 'true') NOT NULL DEFAULT 'false',
        `active` ENUM ('false', 'true') NOT NULL DEFAULT 'false',
        `date` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
        PRIMARY KEY (`id`)
        ) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COLLATE = utf8mb4_general_ci;

INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('verify', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>Verify your ${app_name} account</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>Verify your email address</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>Thanks for signing up for ${app_name}. Please verify your email address by clicking the button below.</p><a class=button href="${app_url}/api/user/auth/verify?code=${token}">Verify Email Address</a><p>If you did not create an account with ${app_name}, you can safely ignore this email.</p></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');
INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('reset_password', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>Reset your ${app_name} password</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>Reset your password</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>We received a request to reset your password for your ${app_name} account. Click the button below to reset it.</p><a class=button href="${app_url}/auth/reset-password?token=${token}">Reset Password</a><p>If you did not request a password reset, you can safely ignore this email.</p></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');
INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('new_login', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>New login to your ${app_name} account</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>New login detected</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>We detected a new login to your ${app_name} account from a new device. If this was you, you can safely ignore this email. If this was not you, please secure your account immediately.</p><a class=button href="${app_url}/auth/reset-password">Secure Account</a></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');
INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('new_invoice', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>New Invoice from ${app_name}</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>New Invoice</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>You have a new invoice from ${app_name}. Please find the details below:</p><p>Invoice Number: ${invoice_number}</p><p>Amount: ${invoice_amount}</p><a class=button href="${app_url}/invoices/${invoice_id}/view">View Invoice</a><p>If you have any questions, please contact our support team.</p></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');
INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('product_ready', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>Your ${app_name} product is ready</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>Your product is ready</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>Your ${app_name} product is ready. You can access it using the following details:</p><p>Location: ${product_location}</p><p>Username: ${username}</p><p>Password: ${password}</p><p>Additional Info: ${additional_info}</p><a class=button href="${product_location}">Access Product</a></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');
INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('product_suspended', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>Your ${app_name} product was suspended</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>Product Suspended</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>Your ${app_name} product was suspended. Please contact support for more information.</p></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');
INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('invoice_paid', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>Invoice Paid - ${app_name}</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>Invoice Paid</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>Thank you for your payment. Your invoice ${invoice_number} has been paid successfully.</p><p>Amount: ${invoice_amount}</p><a class=button href="${app_url}/invoices/${invoice_id}/view">View Invoice</a></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');
INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('welcome', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>Welcome to ${app_name}</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>Welcome to ${app_name}</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>Welcome to ${app_name}! We are excited to have you on board. Get started by exploring our features and services.</p><a class=button href="${app_url}/dashboard">Go to Dashboard</a></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');