INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('server_renewal_reminder', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>Server Renewal Reminder - ${app_name}</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>Server Renewal Reminder</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>Your server (s) will be deleted in 7 days if not renewed. To prevent data loss, please renew your server as soon as possible.</p><p>Server Details:</p><ul><li>Expiration Date: ${expiration_date}</li><li>Renewal Amount: ${renewal_amount}</li></ul><a class=button href="${app_url}/servers/${pterodactyl_id}/renew">Renew Server</a><p>If you do not wish to renew this server, you can safely ignore this email.</p></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');
INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('server_suspended', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>Server Suspended - ${app_name}</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>Server Suspended</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>Your server (s) has been suspended. The server will be deleted in 1 day if not renewed.</p><p>Server Details:</p><ul><li>Suspension Reason: ${suspension_reason}</li><li>Renewal Amount: ${renewal_amount}</li></ul><a class=button href="${app_url}/servers/${pterodactyl_id}/renew">Renew Server</a><p>If you have any questions about the suspension, please contact our support team.</p></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');
INSERT INTO `mythicaldash_mail_templates` (`name`, `content`, `active`) VALUES ('server_deleted', '<!doctypehtml><html lang=en><meta charset=UTF-8><meta content="width=device-width,initial-scale=1"name=viewport><title>Server Deleted - ${app_name}</title><style>body{font-family:Arial,sans-serif;line-height:1.6;color:#333;background-color:#1a103c;margin:0;padding:0}.container{max-width:600px;margin:20px auto;background-color:#fff;border-radius:8px;overflow:hidden}.header{background-color:#1a103c;color:#fff;text-align:center;padding:20px}.content{padding:20px}.button{display:inline-block;background-color:#6366f1;color:#fff;text-decoration:none;padding:10px 20px;border-radius:5px;margin-top:20px}.footer{background-color:#f4f4f4;text-align:center;padding:10px;font-size:12px;color:#666}</style><div class=container><div class=header><h1>${app_name}</h1></div><div class=content><h2>Server Deleted</h2><p>Dear Sir or Madam,</p><p>Hi ${first_name} ${last_name},</p><p>Your server (s) has been deleted due to non-renewal. All data associated with this server has been permanently removed.</p><p>Server Details:</p><ul><li>Deletion Date: ${deletion_date}</li></ul><a class=button href="${app_url}/servers">View Servers</a><p>If you wish to create a new server, you can do so from your dashboard.</p></div><div class=footer><p>© 2025 ${app_name}. All rights reserved.</p></div></div>', 'true');